 496	 // Get current balance
 497	 const userCredit = await tx.query.userCreditsTable.findFirst({
 498	 where: eq(userCreditsTable.userId, userId)
 499	 });
 500	 
 501	 const currentBalance = userCredit?.balance || 0;
 502	 const newBalance = currentBalance + amount;
 503	 
