 505	 if (userCredit) {
 506	 await tx
 507	 .update(userCreditsTable)
 508	 .set({
 509	 balance: newBalance,
 510	 updatedAt: new Date()
 511	 })
 512	 .where(eq(userCreditsTable.userId, userId));
 513	 } else {
 514	 await tx.insert(userCreditsTable).values({
 515	 userId,
 516	 balance: amount,
