 812	 /**
 813	 * Get user usage statistics by external relation ID (our user ID)
 814	 * @param extRelationId The external relation ID (our user ID)
 815	 * @param period Optional period for usage data in YYYY-MM-DD format
 816	 * @param range Optional range for usage data (d=day, w=week, m=month)
 817	 * @returns Usage data from VirtFusion
 818	 */
 819	 async getUserUsageByExtRelationId(extRelationId: number, period?: string, range: string = "m") {
 820	 // Get current date in YYYY-MM-DD format if period is not provided
 821	 if (!period) {
 822	 const today = new Date();
 823	 const year = today.getFullYear();
 824	 const month = String(today.getMonth() + 1).padStart(2, '0');
 825	 const day = String(today.getDate()).padStart(2, '0');
 826	 period = `${year}-${month}-${day}`;
 827	 }
 828	
 829	 const endpoint = `/selfService/usage/byUserExtRelationId/${extRelationId}?period[]=${period}&range=${range}`;
 830	 return this.request("GET", endpoint);
 831	 }
